import { Box, Typography } from "@mui/material";
import React from "react";
import coverpic from "/run/media/ij-singh/My Data/WEB DEVELOPMENT/Full Stack Projects/social-media-app/client/src/img/cover.jpg";
import profilepic from "../../../img/profileImg.jpg";
const ProfileCard = () => {
    return (
        <Box width={"100%"} bgcolor={"green"} borderRadius={"20px"}
            display={"flex"}
            flexDirection={"column"}
            gap={2}
        >
            <Box
                display={"flex"}
                flexDirection={"column"}
                alignItems={'center'}
                position={"relative"}
            >
                <img
                    src={coverpic}
                    style={{
                        width: "100%",
                        borderTopLeftRadius: "20px",
                        borderTopRightRadius: "20px",
                    }}
                />
                <img
                    src={profilepic}
                    style={{
                        width: "30%",
                        borderRadius: "50%",
                        position: "absolute",
                        top: "60%",
                        boxShadow: "0px 4px 17px 2px rgba(0, 0, 0, 0.25)"
                    }}
                />
            </Box>
            <Box
                fullWidth
                textAlign={"center"}
                position={'relative'}
                top={'2.5rem'}
            >

                <Typography fontWeight={"400"} fontSize={"1.2rem"}>Sohan Kumar</Typography>
                <Typography fontWeight={"400"} fontSize={"1rem"}>Senio UI/UX Developer</Typography>
            </Box>

            <Box display={"flex"}
                width={"90%"}
                justifyContent={'space-around'}
                position={'relative'}
                top={'3rem'}
                borderTop={"1px solid black"}
                padding={"10px 30px"}
                margin={"auto"}
            >
                <Box fullWidth
                    margin={'auto'}
                    textAlign={'center'}
                    borderRight={'1px solid black'}
                >
                    <Typography>4000</Typography>
                    <Typography>Followers</Typography>
                </Box>
                <Box
                    fullWidth
                    margin={'auto'}
                    textAlign={'center'}
                >
                    <Typography>400</Typography>

                    <Typography>Following</Typography>
                </Box>

            </Box>
            <Box>
                <Typography>My Profile</Typography>
            </Box>
        </Box >
    );
};

export default ProfileCard;
